import { useState } from 'react'

const Header = ({ text }) => <h1>{text}</h1>

const Button = ({ handleClick, text }) => (
  <button onClick={handleClick}>
    {text}
  </button>
)

const Stats = ({ stat, text}) => (
  <p>{text} {stat}</p>
)

const Statistics = ({ good, neutral, bad, all}) => {
  if (all === 0) {
    return (
      <div>
        No Feedback Given
      </div>
    )
  }
  return (
    <div>
    <h1>Statistics</h1>
      <p>Good {good}</p>
      <p>Neutral {neutral}</p>
      <p>Bad {bad}</p>
      <p>All {all}</p>
      <p>Average {(good - bad) / all}</p>
      <p>Positive {((good / all) * 100).toString() + '%'}</p>
    </div>
  )
}

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const [all, setAll] = useState(0)

  // console.log('good', good)
  // console.log('neu', neutral)
  // console.log('bad', bad)

  const handleGoodClick = () => {
    setGood(good + 1)
    setAll(all + 1)
  }

  const handleNeutralClick = () => {
    setNeutral(neutral + 1)
    setAll(all + 1)
  }

  const handleBadClick = () => {
    setBad(bad + 1)
    setAll(all + 1)
  }

  return (
    <div>
      <Header text={'Give Feedback'} />
      <Button handleClick={handleGoodClick} text='Good' />
      <Button handleClick={handleNeutralClick} text='Neutral' />
      <Button handleClick={handleBadClick} text='Bad' />
      <Statistics good={good} neutral={neutral} bad={bad} all={all} />

      {/* <Header text={'Statistics'} />
      <Stats stat={good} text='Good' />
      <Stats stat={neutral} text='Neutral' />
      <Stats stat={bad} text='Bad' />
      <Stats stat={all} text='all' />
      <Stats stat={(good - bad) / all} text='Average' />
      <Stats stat={((good / all) * 100).toString() + '%'} text='Positive' /> */}

    </div>
  )
}

export default App
